BOOST_AUTO_TEST_CASE(Integer_Factorial) {
    Integer i = 0;
    i.factorial();
    BOOST_TEST(i == 1);

    i = 1;
    i.factorial();
    BOOST_TEST(i == 1);

    i = 2;
    i.factorial();
    BOOST_TEST(i == 2);

    i = 3;
    i.factorial();
    BOOST_TEST(i == 6);

    i = 4;
    i.factorial();
    BOOST_TEST(i == 24);
}

BOOST_AUTO_TEST_CASE(Integer_exptests)
{
	auto a = 25_v;
    a ^= 1_v/2;
    BOOST_TEST(a==5_v*(1_v^(1_v/2)));

    a=-25_v;
    a ^= 1_v/2;
    BOOST_TEST(a==5_v*((-1_v)^(1_v/2)));

    a=25_v;
    a ^= 1_v/-2;
    BOOST_TEST(a==(1_v/5)*((1_v)^(1_v/2)));

    a=25_v;
    a ^= -1_v/2;
    BOOST_TEST(a==(1_v/5)*((1_v)^(1_v/2)));

    a=-25_v;
    a ^= 1_v/-2;
    BOOST_TEST(a==(1_v/5)*((-1_v)^(1_v/2)));

    a=-25_v;
    a ^= -1_v/2;
    BOOST_TEST(a==(1_v/5)*((-1_v)^(1_v/2)));

    a=-25_v;
    a ^= Fraction{-1,-2};
    BOOST_TEST(a==5_v*((-1_v)^(1_v/2)));

    a=8;
    a^=3_v/2;

    a = 5;
    // Fixme: a ^= -.5;
    // Fixme: BOOST_TEST(a == constants::plus_minus_1 / PrincipalSurd{5});
}

