BOOST_AUTO_TEST_CASE(Integer_FirstFactor_LastFactor_tests)
{
    // Test FirstFactor and LastFactor with prime number
    Integer prime = 17;
    BOOST_TEST(prime.FirstFactor() == prime);
    BOOST_TEST(prime.LastFactor() == 1);

    // Test FirstFactor and LastFactor with composite number
    Integer composite = 28;
    BOOST_TEST(composite.FirstFactor() == 2);
    BOOST_TEST(composite.LastFactor() == 14);

    // Test FirstFactor and LastFactor with number one
    Integer one = 1;
    BOOST_TEST(one.FirstFactor() == 1);
    BOOST_TEST(one.LastFactor() == 1);
}

BOOST_AUTO_TEST_CASE(Integer_sqrt_test)
{
    const int_t initial_a = 5;
    int_t a = initial_a, b = 18, z=0;
    for(;b-->z;)
    {
        auto sqroot=a;
        a *= a;

        int_t t;
        std::cout << omnn::measure::nanoseconds([&](){
            t = boost::multiprecision::sqrt(a.ca());
            auto match = t != sqroot.ca();

            if(match)
            {
                t = boost::multiprecision::sqrt(a.a());
            }
        }) << std::endl;
        BOOST_TEST(t==sqroot);
    }

    auto _ = 4_v;
    auto _2 = (_ / 2).Sqrt();
    auto _1 = _ / _2;
    BOOST_TEST(_1 == _2*2);
    _ ^= 1_v/2;
    BOOST_TEST(_.Sq()==4_v);
    BOOST_TEST(_==2*((1)^(1_v/2)));
    _1 = _;
    _ *= _1;
    BOOST_TEST(_==4_v);

    _ = 24;
    _1 = _.Sqrt();
    _2 = PrincipalSurd(6) * 2;
    BOOST_TEST(_1 == _2);
}
